import React from "react";

const FIELDS = [
  { key: "name", label: "정책명" },
  { key: "target", label: "지원대상" },
  { key: "content", label: "지원내용" },
  { key: "period", label: "신청기간" },
];

function PolicyCompare({ policies }) {
  const [left, right] = policies;

  if (!left && !right) return null;

  return (
    <div style={{ marginTop: 40 }}>
      <h2>정책 비교</h2>
      <table border="1" cellPadding="8" style={{ width: "100%", textAlign: "center" }}>
        <thead>
          <tr>
            <th style={{ width: "35%" }}>정책 1</th>
            <th style={{ width: "30%" }}>항목</th>
            <th style={{ width: "35%" }}>정책 2</th>
          </tr>
        </thead>
        <tbody>
          {FIELDS.map(field => (
            <tr key={field.key}>
              <td>{left ? left[field.key] : "-"}</td>
              <td><strong>{field.label}</strong></td>
              <td>{right ? right[field.key] : "-"}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default PolicyCompare;
