// Chatbot.js 내 메시지 파서 확장
const MessageParser = ({ actions }) => {
    const parse = (message) => {
      if (message.includes('VS')) {
        const [district1, district2] = message.split('VS');
        actions.handleCompare([district1.trim(), district2.trim()]);
      }
    };
    return parse;
  };

  // ActionProvider 확장
  class ActionProvider {
    constructor(createChatBotMessage, setState, handleCompare) {
      this.handleCompare = handleCompare;
    }

    handleCompare = (districts) => {
      this.handleCompare(districts);
      const message = this.createChatBotMessage(`${districts.join(' vs ')} 비교 결과입니다.`);
      this.updateChatState(message);
    };
  }
