import React from "react";
import { 
  Paper, 
  Box, 
  Typography, 
  Divider, 
  Grid,
  Card,
  CardHeader,
  CardContent,
  Alert,
  useTheme
} from '@mui/material';
import CompareArrowsIcon from '@mui/icons-material/CompareArrows';

// ÎπÑÍµêÌï† Ìï≠Î™©ÏùÑ FIELDSÏóê Ï†ïÏùò
const FIELDS = [
  { key: "sprtTrgtCn", label: "ÏßÄÏõêÎåÄÏÉÅ", icon: "üë•" },
  { key: "wlfareInfoReldCn", label: "Î¨∏ÏùòÏ≤ò Ïó∞ÎùΩÏ≤ò", icon: "üìû" },
  { key: "wlfareInfoReldNm", label: "Î¨∏ÏùòÏ≤ò Ïù¥Î¶Ñ", icon: "üè¢" },
  { key: "aplyMtdCn", label: "Ïã†Ï≤≠Î∞©Î≤ï", icon: "üìù" },
  { key: "alwServCn", label: "ÏßÄÏõêÎÇ¥Ïö©", icon: "üí∞" },
  { key: "basfrmList", label: "Í¥ÄÎ†® ÏûêÎ£å ÎßÅÌÅ¨", icon: "üîó" }
];

// Í∞Å Ï†ïÏ±ÖÏóêÏÑú ÎπÑÍµê Ìï≠Î™© Í∞íÏùÑ Ï∂îÏ∂úÌïòÎäî Ìï®Ïàò
function getFieldValue(policy, key) {
  if (!policy || !policy.json || !policy.json.wantedDtl) return "-";
  const wantedDtl = policy.json.wantedDtl;
  switch (key) {
    case "sprtTrgtCn":
      return wantedDtl.sprtTrgtCn || "-";
    case "wlfareInfoReldCn":
      return wantedDtl.inqplCtadrList?.wlfareInfoReldCn || "-";
    case "wlfareInfoReldNm":
      return wantedDtl.inqplCtadrList?.wlfareInfoReldNm || "-";
    case "aplyMtdCn":
      return wantedDtl.aplyMtdCn || "-";
    case "alwServCn":
      return wantedDtl.alwServCn || "-";
    case "basfrmList":
      // basfrmListÍ∞Ä Î∞∞Ïó¥ ÎòêÎäî Í∞ùÏ≤¥Ïùº Ïàò ÏûàÏùå
      if (Array.isArray(wantedDtl.basfrmList)) {
        // Ïó¨Îü¨ Í∞úÎ©¥ Ï≤´ Î≤àÏß∏Îßå ÌëúÏãú, ÌïÑÏöîÏãú mapÏúºÎ°ú Ïó¨Îü¨ Í∞ú ÌëúÏãú Í∞ÄÎä•
        return wantedDtl.basfrmList[0]?.wlfareInfoReldCn
          ? <a href={wantedDtl.basfrmList[0].wlfareInfoReldCn} target="_blank" rel="noopener noreferrer">{wantedDtl.basfrmList[0].wlfareInfoReldNm || "Í¥ÄÎ†® ÏûêÎ£å"}</a>
          : "-";
      } else if (wantedDtl.basfrmList?.wlfareInfoReldCn) {
        // Í∞ùÏ≤¥Ïùº Í≤ΩÏö∞
        return <a href={wantedDtl.basfrmList.wlfareInfoReldCn} target="_blank" rel="noopener noreferrer">{wantedDtl.basfrmList.wlfareInfoReldNm || "Í¥ÄÎ†® ÏûêÎ£å"}</a>;
      }
      return "-";
    default:
      return "-";
  }
}

// ÎπÑÍµê Ìï≠Î™© Ïπ¥Îìú Ïª¥Ìè¨ÎÑåÌä∏
const CompareItem = ({ field, leftPolicy, rightPolicy }) => {
  const theme = useTheme();

  return (
    <Card variant="outlined" sx={{ mb: 2, borderRadius: 2, overflow: 'hidden' }}>
      <CardHeader
        title={
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <Typography variant="span" sx={{ mr: 1, fontSize: '1.2rem' }}>{field.icon}</Typography>
            <Typography variant="h6">{field.label}</Typography>
          </Box>
        }
        sx={{ 
          backgroundColor: theme.palette.grey[50],
          borderBottom: `1px solid ${theme.palette.grey[200]}`,
          py: 1.5
        }}
      />
      <CardContent sx={{ p: 0 }}>
        <Grid container>
          <Grid item xs={6} sx={{ 
            p: 2,
            borderRight: `1px solid ${theme.palette.grey[200]}`,
            backgroundColor: theme.palette.background.paper
          }}>
            <Typography variant="body1">{getFieldValue(leftPolicy, field.key)}</Typography>
          </Grid>
          <Grid item xs={6} sx={{ 
            p: 2,
            backgroundColor: theme.palette.background.paper
          }}>
            <Typography variant="body1">{getFieldValue(rightPolicy, field.key)}</Typography>
          </Grid>
        </Grid>
      </CardContent>
    </Card>
  );
};

function PolicyCompare({ policies = [] }) {
  const theme = useTheme();

  // Ï†ïÏ±ÖÏù¥ Ï∂©Î∂ÑÌïòÏßÄ ÏïäÏùÄ Í≤ΩÏö∞ Ï≤òÎ¶¨
  if (!policies || policies.length < 2) {
    return (
      <Box sx={{ my: 3 }}>
        <Alert severity="info" sx={{ borderRadius: 2 }}>
          ÎπÑÍµêÌï† Ï†ïÏ±ÖÏù¥ Ï∂©Î∂ÑÌïòÏßÄ ÏïäÏäµÎãàÎã§. Îëê Í∞úÏùò Ï†ïÏ±ÖÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.
        </Alert>
      </Box>
    );
  }

  const [left, right] = policies;
  const leftName = left?.json?.wantedDtl?.servNm || "Ï†ïÏ±Ö 1";
  const rightName = right?.json?.wantedDtl?.servNm || "Ï†ïÏ±Ö 2";

  return (
    <Box sx={{ my: 4 }}>
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
        <CompareArrowsIcon sx={{ mr: 1, color: theme.palette.primary.main }} />
        <Typography variant="h5" component="h2" sx={{ fontWeight: 500 }}>
          Ï†ïÏ±Ö ÎπÑÍµê
        </Typography>
      </Box>

      <Paper elevation={0} sx={{ 
        borderRadius: 3, 
        overflow: 'hidden',
        border: `1px solid ${theme.palette.grey[200]}`,
        mb: 3
      }}>
        <Grid container>
          <Grid item xs={6} sx={{ 
            p: 2, 
            borderRight: `1px solid ${theme.palette.grey[200]}`,
            backgroundColor: theme.palette.primary.main,
            color: 'white'
          }}>
            <Typography variant="h6" sx={{ fontWeight: 'bold' }}>{leftName}</Typography>
          </Grid>
          <Grid item xs={6} sx={{ 
            p: 2,
            backgroundColor: theme.palette.primary.main,
            color: 'white'
          }}>
            <Typography variant="h6" sx={{ fontWeight: 'bold' }}>{rightName}</Typography>
          </Grid>
        </Grid>
      </Paper>

      <Box>
        {FIELDS.map((field) => (
          <CompareItem 
            key={field.key} 
            field={field} 
            leftPolicy={left} 
            rightPolicy={right} 
          />
        ))}
      </Box>
    </Box>
  );
}

export default PolicyCompare;
