import React, { useState } from "react";
import { Box, TextField, Button, Paper } from "@mui/material";
import { styled } from "@mui/material/styles";

// 메인 컬러 상수
const MAIN_COLOR = "#6D4FC2";

// 스타일링된 폼 컨테이너
const ChatFormContainer = styled(Paper)(({ theme }) => ({
  display: "flex",
  alignItems: "center",
  padding: theme.spacing(1.5),
  borderRadius: theme.shape.borderRadius * 2,
  background: "#f7f4fd",
  boxShadow: "0 2px 8px rgba(109, 79, 194, 0.06)",
}));

function ChatForm({ onSend }) {
  const [input, setInput] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    onSend(input);
    setInput("");
  };

  return (
    <ChatFormContainer
      component="form"
      onSubmit={handleSubmit}
      elevation={0}
    >
      <TextField
        fullWidth
        variant="outlined"
        placeholder="메시지를 입력하세요"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        required
        sx={{
          background: "#fff",
          borderRadius: 2,
          "& .MuiOutlinedInput-root": {
            "& fieldset": {
              borderColor: MAIN_COLOR,
            },
            "&:hover fieldset": {
              borderColor: MAIN_COLOR,
            },
            "&.Mui-focused fieldset": {
              borderColor: MAIN_COLOR,
            },
          },
        }}
        size="small"
      />
      <Button
        type="submit"
        variant="contained"
        sx={{
          ml: 2,
          backgroundColor: MAIN_COLOR,
          borderRadius: 2,
          fontWeight: "bold",
          boxShadow: "none",
          "&:hover": {
            backgroundColor: "#5940a9",
          },
        }}
        disableElevation
      >
        전송
      </Button>
    </ChatFormContainer>
  );
}

export default ChatForm;
