import React from "react";

function PolicySearch({ policies, selected, toggleSelect }) {
  // 데이터가 로드 중인지 확인
  if (!policies || policies.length === 0) {
    return <div>정책 데이터를 불러오는 중입니다...</div>;
  }

  // 정책 데이터에서 필요한 정보 추출
  const mappedPolicies = policies.map(policy => {
    // service_id를 id로 사용
    return {
      id: policy.service_id,
      name: policy.json?.wantedDtl?.servNm || "제목 없음",
      target: policy.json?.wantedDtl?.sprtTrgtCn || "대상 정보 없음"
    };
  });

  return (
    <div>
      <h2>정책 목록</h2>
      {mappedPolicies.length > 0 ? (
        <ul>
          {mappedPolicies.map((policy) => (
            <li key={policy.id}>
              <label>
                <input
                  type="checkbox"
                  checked={selected.includes(policy.id)}
                  onChange={() => toggleSelect(policy.id)}
                />
                {policy.name} ({policy.target})
              </label>
            </li>
          ))}
        </ul>
      ) : (
        <p>표시할 정책이 없습니다.</p>
      )}
    </div>
  );
}

export default PolicySearch;
